mapper.select:
select

from lessontype


		lessonId = #{lessonId,jdbcType=VARCHAR}


		courseCnt = #{coursecnt,jdbcType=SMALLINT}


		and lessonType = #{lessontype,jdbcType=INTEGER}

mapper.delete:
delete from
lessontype
where courseCnt = #{coursecnt,jdbcType=SMALLINT}
and
lessonType = #{lessontype,jdbcType=INTEGER}
