mapper.insert:
insert into video_course (video_course_id, video_course_name, grade_id, 
  semaster, course_id, video_course_type, 
  h5_video_link, status, chapter_id
  )
values (#{videoCourseId,jdbcType=VARCHAR}, #{videoCourseName,jdbcType=VARCHAR}, #{gradeId,jdbcType=SMALLINT}, 
  #{semaster,jdbcType=TINYINT}, #{courseId,jdbcType=SMALLINT}, #{videoCourseType,jdbcType=TINYINT}, 
  #{h5VideoLink,jdbcType=VARCHAR}, #{status,jdbcType=TINYINT}, #{chapterId,jdbcType=VARCHAR}
  )

mapper.select:
SELECT grade_id AS gradeId,semaster AS semaster,video_course_name AS videoName,h5_video_link AS h5VideoLink FROM video_course


		AND grade_id  = #{gradeId}


		AND course_id = #{courseId}


		AND semaster  = #{semaster}


		AND video_course_type = #{videoCourseType}


		AND video_course_name LIKE CONCAT('%',#{name},'%')
